var comInfo=document.getElementsByClassName("com-info"),companys=document.getElementsByClassName("company-con");console.log(companys);for(var i=0;i<comInfo.length;i++)comInfo[i].onclick=function(){for(var c in companys){companys[c].className="company-con";var n=this.getAttribute("lcl")}companys[n].className="company-con active"};for(var expR=document.getElementsByClassName("exp-r"),i=0;i<expR.length;i++)expR[i].firstElementChild.onclick=function(){for(var c in companys)companys[c].className="company-con";companys[3].className="company-con active"};